      feq_BE   =   c1o54* (drho+3.0f*( vx1    -vx3)+c9o2*( vx1    -vx3)*( vx1    -vx3)-cu_sq);
      feq_TN   =   c1o54* (drho+3.0f*(     vx2+vx3)+c9o2*(     vx2+vx3)*(     vx2+vx3)-cu_sq);
      feq_BN   =   c1o54* (drho+3.0f*(     vx2-vx3)+c9o2*(     vx2-vx3)*(     vx2-vx3)-cu_sq);
      feq_TNE  =   c1o216*(drho+3.0f*( vx1+vx2+vx3)+c9o2*( vx1+vx2+vx3)*( vx1+vx2+vx3)-cu_sq);
