      feq_TSE  =   c1o216*(drho+3.0f*( vx1-vx2+vx3)+c9o2*( vx1-vx2+vx3)*( vx1-vx2+vx3)-cu_sq);
      feq_BSE  =   c1o216*(drho+3.0f*( vx1-vx2-vx3)+c9o2*( vx1-vx2-vx3)*( vx1-vx2-vx3)-cu_sq);
      feq_W    =   feq_E   + c2o27* (6.0f*(-vx1        ));
      feq_S    =   feq_N   + c2o27* (6.0f*(    -vx2    ));
      feq_B    =   feq_T   + c2o27* (6.0f*(        -vx3));
      feq_SW   =   feq_NE  + c1o54* (6.0f*(-vx1-vx2    ));
      feq_NW   =   feq_SE  + c1o54* (6.0f*(-vx1+vx2    ));
      feq_BW   =   feq_TE  + c1o54* (6.0f*(-vx1    -vx3));
      feq_TW   =   feq_BE  + c1o54* (6.0f*(-vx1    +vx3));
      feq_BS   =   feq_TN  + c1o54* (6.0f*(    -vx2-vx3));
      feq_TS   =   feq_BN  + c1o54* (6.0f*(    -vx2+vx3));
      feq_BSW  =   feq_TNE + c1o216*(6.0f*(-vx1-vx2-vx3));
      feq_TSW  =   feq_BNE + c1o216*(6.0f*(-vx1-vx2+vx3));
      feq_BNW  =   feq_TSE + c1o216*(6.0f*(-vx1+vx2-vx3));
      feq_TNW  =   feq_BSE + c1o216*(6.0f*(-vx1+vx2+vx3));

      //////////////////////////////////////////////////////////////////////////
      //index 
      kb   = kzero;   
      kbw  = kw;  
      kbs  = ks;  
      kbsw = ksw; 
      kzero= k0w;
      kw   = neighborFX[k0w];   
      ks   = k0sw;   
      ksw  = neighborFX[k0sw];  
      ////////////////////////////////////////////////////////////////////////////////
      feF[kzero]   = f_E    + x_E    + y_E    - z_E    + xy_E    - xz_E    - yz_E    + feq_E;
