      fseF[ks]     = f_SE   + x_SE   + y_SE   - z_SE   + xy_SE   - xz_SE   - yz_SE   + feq_SE;
      fnwF[kw]     = f_SE   + x_SE   + y_SE   - z_SE   + xy_SE   - xz_SE   - yz_SE   + feq_NW;
      fteF[kzero]  = f_TE   + x_TE   + y_TE   - z_TE   + xy_TE   - xz_TE   - yz_TE   + feq_TE;
      fbwF[kbw]    = f_TE   + x_TE   + y_TE   - z_TE   + xy_TE   - xz_TE   - yz_TE   + feq_BW;
      fbeF[kb]     = f_BE   + x_BE   + y_BE   - z_BE   + xy_BE   - xz_BE   - yz_BE   + feq_BE;
      ftwF[kw]     = f_BE   + x_BE   + y_BE   - z_BE   + xy_BE   - xz_BE   - yz_BE   + feq_TW;
      ftnF[kzero]  = f_TN   + x_TN   + y_TN   - z_TN   + xy_TN   - xz_TN   - yz_TN   + feq_TN;
      fbsF[kbs]    = f_TN   + x_TN   + y_TN   - z_TN   + xy_TN   - xz_TN   - yz_TN   + feq_BS;
      fbnF[kb]     = f_BN   + x_BN   + y_BN   - z_BN   + xy_BN   - xz_BN   - yz_BN   + feq_BN;
      ftsF[ks]     = f_BN   + x_BN   + y_BN   - z_BN   + xy_BN   - xz_BN   - yz_BN   + feq_TS;
      fzeroF[kzero]= f_ZERO + x_ZERO + y_ZERO - z_ZERO                               + feq_ZERO;
      ftneF[kzero] = f_TNE  + x_TNE  + y_TNE  - z_TNE  + xy_TNE  - xz_TNE  - yz_TNE  + feq_TNE;
