      fbneF[kb]    = f_TSW  + x_TSW  + y_TSW  - z_TSW  + xy_TSW  - xz_TSW  - yz_TSW  + feq_BNE;
      fbswF[kbsw]  = f_TNE  + x_TNE  + y_TNE  - z_TNE  + xy_TNE  - xz_TNE  - yz_TNE  + feq_BSW;
      fbseF[kbs]   = f_TNW  + x_TNW  + y_TNW  - z_TNW  + xy_TNW  - xz_TNW  - yz_TNW  + feq_BSE;
      fbnwF[kbw]   = f_TSE  + x_TSE  + y_TSE  - z_TSE  + xy_TSE  - xz_TSE  - yz_TSE  + feq_BNW;
