      feq_S    =   feq_N   + c2o27* (c6o1*(    -vx2    ));
      feq_B    =   feq_T   + c2o27* (c6o1*(        -vx3));
      feq_SW   =   feq_NE  + c1o54* (c6o1*(-vx1-vx2    ));
      feq_NW   =   feq_SE  + c1o54* (c6o1*(-vx1+vx2    ));
      feq_BW   =   feq_TE  + c1o54* (c6o1*(-vx1    -vx3));
      feq_TW   =   feq_BE  + c1o54* (c6o1*(-vx1    +vx3));
      feq_BS   =   feq_TN  + c1o54* (c6o1*(    -vx2-vx3));
      feq_TS   =   feq_BN  + c1o54* (c6o1*(    -vx2+vx3));
      feq_BSW  =   feq_TNE + c1o216*(c6o1*(-vx1-vx2-vx3));
      feq_TSW  =   feq_BNE + c1o216*(c6o1*(-vx1-vx2+vx3));
      feq_BNW  =   feq_TSE + c1o216*(c6o1*(-vx1+vx2-vx3));
      feq_TNW  =   feq_BSE + c1o216*(c6o1*(-vx1+vx2+vx3));

      //////////////////////////////////////////////////////////////////////////
      //index 
      kzero= kw;
      kw   = neighborFX[kw];   
      ks   = ksw;   
      kb   = kbw;   
      ksw  = neighborFX[ksw];  
      kbw  = neighborFX[kbw];  
      kbs  = kbsw;  
      kbsw = neighborFX[kbsw]; 
      ////////////////////////////////////////////////////////////////////////////////
      feF[kzero]   = f_E    + x_E    + y_E    + z_E    + xy_E    + xz_E    + yz_E    + feq_E;
      fwF[kw]      = f_E    + x_E    + y_E    + z_E    + xy_E    + xz_E    + yz_E    + feq_W;
      fnF[kzero]   = f_N    + x_N    + y_N    + z_N    + xy_N    + xz_N    + yz_N    + feq_N;
      fsF[ks]      = f_N    + x_N    + y_N    + z_N    + xy_N    + xz_N    + yz_N    + feq_S;
      ftF[kzero]   = f_T    + x_T    + y_T    + z_T    + xy_T    + xz_T    + yz_T    + feq_T;
      fbF[kb]      = f_T    + x_T    + y_T    + z_T    + xy_T    + xz_T    + yz_T    + feq_B;
      fneF[kzero]  = f_NE   + x_NE   + y_NE   + z_NE   + xy_NE   + xz_NE   + yz_NE   + feq_NE;
      fswF[ksw]    = f_NE   + x_NE   + y_NE   + z_NE   + xy_NE   + xz_NE   + yz_NE   + feq_SW;
      fseF[ks]     = f_SE   + x_SE   + y_SE   + z_SE   + xy_SE   + xz_SE   + yz_SE   + feq_SE;
      fnwF[kw]     = f_SE   + x_SE   + y_SE   + z_SE   + xy_SE   + xz_SE   + yz_SE   + feq_NW;
      fteF[kzero]  = f_TE   + x_TE   + y_TE   + z_TE   + xy_TE   + xz_TE   + yz_TE   + feq_TE;
      fbwF[kbw]    = f_TE   + x_TE   + y_TE   + z_TE   + xy_TE   + xz_TE   + yz_TE   + feq_BW;
      fbeF[kb]     = f_BE   + x_BE   + y_BE   + z_BE   + xy_BE   + xz_BE   + yz_BE   + feq_BE;
      ftwF[kw]     = f_BE   + x_BE   + y_BE   + z_BE   + xy_BE   + xz_BE   + yz_BE   + feq_TW;
      ftnF[kzero]  = f_TN   + x_TN   + y_TN   + z_TN   + xy_TN   + xz_TN   + yz_TN   + feq_TN;
      fbsF[kbs]    = f_TN   + x_TN   + y_TN   + z_TN   + xy_TN   + xz_TN   + yz_TN   + feq_BS;
      fbnF[kb]     = f_BN   + x_BN   + y_BN   + z_BN   + xy_BN   + xz_BN   + yz_BN   + feq_BN;
      ftsF[ks]     = f_BN   + x_BN   + y_BN   + z_BN   + xy_BN   + xz_BN   + yz_BN   + feq_TS;
      fzeroF[kzero]= f_ZERO + x_ZERO + y_ZERO + z_ZERO                               + feq_ZERO;
      ftneF[kzero] = f_TNE  + x_TNE  + y_TNE  + z_TNE  + xy_TNE  + xz_TNE  + yz_TNE  + feq_TNE;
      ftswF[ksw]   = f_TSW  + x_TSW  + y_TSW  + z_TSW  + xy_TSW  + xz_TSW  + yz_TSW  + feq_TSW;
      ftseF[ks]    = f_TSE  + x_TSE  + y_TSE  + z_TSE  + xy_TSE  + xz_TSE  + yz_TSE  + feq_TSE;
      ftnwF[kw]    = f_TNW  + x_TNW  + y_TNW  + z_TNW  + xy_TNW  + xz_TNW  + yz_TNW  + feq_TNW;
      fbneF[kb]    = f_TSW  + x_TSW  + y_TSW  + z_TSW  + xy_TSW  + xz_TSW  + yz_TSW  + feq_BNE;
      fbswF[kbsw]  = f_TNE  + x_TNE  + y_TNE  + z_TNE  + xy_TNE  + xz_TNE  + yz_TNE  + feq_BSW;
      fbseF[kbs]   = f_TNW  + x_TNW  + y_TNW  + z_TNW  + xy_TNW  + xz_TNW  + yz_TNW  + feq_BSE;
      fbnwF[kbw]   = f_TSE  + x_TSE  + y_TSE  + z_TSE  + xy_TSE  + xz_TSE  + yz_TSE  + feq_BNW;


      //Position NEB 0.25, 0.25, -0.25
      //x =  c1o4;
      //y =  c1o4;
      //z = -c1o4;

      //drho = c27o64*drho_NEB+c9o64*drho_NET+c9o64*drho_NWB+c3o64*drho_NWT+c9o64*drho_SEB+c3o64*drho_SET+c3o64*drho_SWB+c1o64*drho_SWT;
      drho = drho_NET * (0.140625 - 0.1875 * xoff - 0.1875 * yoff - 0.5625 * zoff) + 
             drho_NWT * (0.046875 + 0.1875 * xoff - 0.0625 * yoff - 0.1875 * zoff) + 
             drho_SET * (0.046875 - 0.0625 * xoff + 0.1875 * yoff - 0.1875 * zoff) + 
             drho_SWT * (0.015625 + 0.0625 * xoff + 0.0625 * yoff - 0.0625 * zoff) + 
             drho_SWB * (0.046875 + 0.1875 * xoff + 0.1875 * yoff + 0.0625 * zoff) + 
             drho_NWB * (0.140625 + 0.5625 * xoff - 0.1875 * yoff + 0.1875 * zoff) + 
             drho_SEB * (0.140625 - 0.1875 * xoff + 0.5625 * yoff + 0.1875 * zoff) + 
             drho_NEB * (0.421875 - 0.5625 * xoff - 0.5625 * yoff + 0.5625 * zoff);
      vx1  = a0 + c1o4*( ax + ay - az) + c1o16*(axx + axy - axz + ayy - ayz + azz) + c1o64*(-axyz);
      vx2  = b0 + c1o4*( bx + by - bz) + c1o16*(bxx + bxy - bxz + byy - byz + bzz) + c1o64*(-bxyz);
      vx3  = c0 + c1o4*( cx + cy - cz) + c1o16*(cxx + cxy - cxz + cyy - cyz + czz) + c1o64*(-cxyz);

      cu_sq=c3o2*(vx1*vx1+vx2*vx2+vx3*vx3);

      feq_ZERO =   c8o27* (drho-cu_sq);
      feq_E    =   c2o27* (drho+c3o1*( vx1        )+c9o2*( vx1        )*( vx1        )-cu_sq);
      feq_N    =   c2o27* (drho+c3o1*(    vx2     )+c9o2*(     vx2    )*(     vx2    )-cu_sq);
      feq_T    =   c2o27* (drho+c3o1*(         vx3)+c9o2*(         vx3)*(         vx3)-cu_sq);
      feq_NE   =   c1o54* (drho+c3o1*( vx1+vx2    )+c9o2*( vx1+vx2    )*( vx1+vx2    )-cu_sq);
      feq_SE   =   c1o54* (drho+c3o1*( vx1-vx2    )+c9o2*( vx1-vx2    )*( vx1-vx2    )-cu_sq);
      feq_TE   =   c1o54* (drho+c3o1*( vx1    +vx3)+c9o2*( vx1    +vx3)*( vx1    +vx3)-cu_sq);
      feq_BE   =   c1o54* (drho+c3o1*( vx1    -vx3)+c9o2*( vx1    -vx3)*( vx1    -vx3)-cu_sq);
      feq_TN   =   c1o54* (drho+c3o1*(     vx2+vx3)+c9o2*(     vx2+vx3)*(     vx2+vx3)-cu_sq);
      feq_BN   =   c1o54* (drho+c3o1*(     vx2-vx3)+c9o2*(     vx2-vx3)*(     vx2-vx3)-cu_sq);
      feq_TNE  =   c1o216*(drho+c3o1*( vx1+vx2+vx3)+c9o2*( vx1+vx2+vx3)*( vx1+vx2+vx3)-cu_sq);
      feq_BNE  =   c1o216*(drho+c3o1*( vx1+vx2-vx3)+c9o2*( vx1+vx2-vx3)*( vx1+vx2-vx3)-cu_sq);
      feq_TSE  =   c1o216*(drho+c3o1*( vx1-vx2+vx3)+c9o2*( vx1-vx2+vx3)*( vx1-vx2+vx3)-cu_sq);
      feq_BSE  =   c1o216*(drho+c3o1*( vx1-vx2-vx3)+c9o2*( vx1-vx2-vx3)*( vx1-vx2-vx3)-cu_sq);
      feq_W    =   feq_E   + c2o27* (c6o1*(-vx1        ));
      feq_S    =   feq_N   + c2o27* (c6o1*(    -vx2    ));
      feq_B    =   feq_T   + c2o27* (c6o1*(        -vx3));
      feq_SW   =   feq_NE  + c1o54* (c6o1*(-vx1-vx2    ));
      feq_NW   =   feq_SE  + c1o54* (c6o1*(-vx1+vx2    ));
      feq_BW   =   feq_TE  + c1o54* (c6o1*(-vx1    -vx3));
      feq_TW   =   feq_BE  + c1o54* (c6o1*(-vx1    +vx3));
      feq_BS   =   feq_TN  + c1o54* (c6o1*(    -vx2-vx3));
      feq_TS   =   feq_BN  + c1o54* (c6o1*(    -vx2+vx3));
      feq_BSW  =   feq_TNE + c1o216*(c6o1*(-vx1-vx2-vx3));
      feq_TSW  =   feq_BNE + c1o216*(c6o1*(-vx1-vx2+vx3));
      feq_BNW  =   feq_TSE + c1o216*(c6o1*(-vx1+vx2-vx3));
      feq_TNW  =   feq_BSE + c1o216*(c6o1*(-vx1+vx2+vx3));

      //////////////////////////////////////////////////////////////////////////
      //index 
      kb   = kzero;   
      kbw  = kw;  
      kbs  = ks;  
      kbsw = ksw; 
      kzero= k0w;
      kw   = neighborFX[k0w];   
      ks   = k0sw;   
      ksw  = neighborFX[k0sw];  
      ////////////////////////////////////////////////////////////////////////////////
      feF[kzero]   = f_E    + x_E    + y_E    - z_E    + xy_E    - xz_E    - yz_E    + feq_E;
      fwF[kw]      = f_E    + x_E    + y_E    - z_E    + xy_E    - xz_E    - yz_E    + feq_W;
      fnF[kzero]   = f_N    + x_N    + y_N    - z_N    + xy_N    - xz_N    - yz_N    + feq_N;
      fsF[ks]      = f_N    + x_N    + y_N    - z_N    + xy_N    - xz_N    - yz_N    + feq_S;
      ftF[kzero]   = f_T    + x_T    + y_T    - z_T    + xy_T    - xz_T    - yz_T    + feq_T;
      fbF[kb]      = f_T    + x_T    + y_T    - z_T    + xy_T    - xz_T    - yz_T    + feq_B;
      fneF[kzero]  = f_NE   + x_NE   + y_NE   - z_NE   + xy_NE   - xz_NE   - yz_NE   + feq_NE;
      fswF[ksw]    = f_NE   + x_NE   + y_NE   - z_NE   + xy_NE   - xz_NE   - yz_NE   + feq_SW;
      fseF[ks]     = f_SE   + x_SE   + y_SE   - z_SE   + xy_SE   - xz_SE   - yz_SE   + feq_SE;
      fnwF[kw]     = f_SE   + x_SE   + y_SE   - z_SE   + xy_SE   - xz_SE   - yz_SE   + feq_NW;
      fteF[kzero]  = f_TE   + x_TE   + y_TE   - z_TE   + xy_TE   - xz_TE   - yz_TE   + feq_TE;
      fbwF[kbw]    = f_TE   + x_TE   + y_TE   - z_TE   + xy_TE   - xz_TE   - yz_TE   + feq_BW;
      fbeF[kb]     = f_BE   + x_BE   + y_BE   - z_BE   + xy_BE   - xz_BE   - yz_BE   + feq_BE;
      ftwF[kw]     = f_BE   + x_BE   + y_BE   - z_BE   + xy_BE   - xz_BE   - yz_BE   + feq_TW;
      ftnF[kzero]  = f_TN   + x_TN   + y_TN   - z_TN   + xy_TN   - xz_TN   - yz_TN   + feq_TN;
      fbsF[kbs]    = f_TN   + x_TN   + y_TN   - z_TN   + xy_TN   - xz_TN   - yz_TN   + feq_BS;
      fbnF[kb]     = f_BN   + x_BN   + y_BN   - z_BN   + xy_BN   - xz_BN   - yz_BN   + feq_BN;
      ftsF[ks]     = f_BN   + x_BN   + y_BN   - z_BN   + xy_BN   - xz_BN   - yz_BN   + feq_TS;
      fzeroF[kzero]= f_ZERO + x_ZERO + y_ZERO - z_ZERO                               + feq_ZERO;
      ftneF[kzero] = f_TNE  + x_TNE  + y_TNE  - z_TNE  + xy_TNE  - xz_TNE  - yz_TNE  + feq_TNE;
      ftswF[ksw]   = f_TSW  + x_TSW  + y_TSW  - z_TSW  + xy_TSW  - xz_TSW  - yz_TSW  + feq_TSW;
      ftseF[ks]    = f_TSE  + x_TSE  + y_TSE  - z_TSE  + xy_TSE  - xz_TSE  - yz_TSE  + feq_TSE;
      ftnwF[kw]    = f_TNW  + x_TNW  + y_TNW  - z_TNW  + xy_TNW  - xz_TNW  - yz_TNW  + feq_TNW;
      fbneF[kb]    = f_TSW  + x_TSW  + y_TSW  - z_TSW  + xy_TSW  - xz_TSW  - yz_TSW  + feq_BNE;
      fbswF[kbsw]  = f_TNE  + x_TNE  + y_TNE  - z_TNE  + xy_TNE  - xz_TNE  - yz_TNE  + feq_BSW;
      fbseF[kbs]   = f_TNW  + x_TNW  + y_TNW  - z_TNW  + xy_TNW  - xz_TNW  - yz_TNW  + feq_BSE;
      fbnwF[kbw]   = f_TSE  + x_TSE  + y_TSE  - z_TSE  + xy_TSE  - xz_TSE  - yz_TSE  + feq_BNW;
   }
