      (D7F.f[0])[kzero] = M0-Mzz-Mxx-Myy;
      (D7F.f[1])[kzero] =  c1o2*Mx +c1o2*Mxx;
      (D7F.f[2])[kw   ] =  c1o2*Mxx-c1o2*Mx;
      (D7F.f[3])[kzero] =  c1o2*My +c1o2*Myy;
      (D7F.f[4])[ks   ] =  c1o2*Myy-c1o2*My;
      (D7F.f[5])[kzero] =  c1o2*Mzz+c1o2*Mz;
      (D7F.f[6])[kb   ] = -c1o2*Mz +c1o2*Mzz;

      ////////////////////////////////////////////////////////////////////////////////
      //Position NEB 0.25, 0.25, -0.25
      ////////////////////////////////////////////////////////////////////////////////
      //x =  c1o4;
      //y =  c1o4;
      //z = -c1o4;

      //////////////////////////////////////////////////////////////////////////
      //index 
      kb   = kzero;   
      kbw  = kw;  
      kbs  = ks;  
      kbsw = ksw; 
      kzero= k0w;
      kw   = neighborFX[k0w];   
      ks   = k0sw;   
      ksw  = neighborFX[k0sw];  
      ////////////////////////////////////////////////////////////////////////////////
      vx1=feF[kzero]+fneF[kzero]+fseF[ks]+fteF[kzero]+fbeF[kb]-fwF[kw]-fnwF[kw]-fswF[ksw]-ftwF[kw]-fbwF[kbw]+ftneF[kzero]-ftswF[ksw]+ftseF[ks]-ftnwF[kw]+fbneF[kb]-fbswF[kbsw]+fbseF[kbs]-fbnwF[kbw];
      vx2=fnF[kzero]+fneF[kzero]+fnwF[kw]+ftnF[kzero]+fbnF[kb]-fsF[ks]-fseF[ks]-fswF[ksw]-ftsF[ks]-fbsF[kbs]+ftneF[kzero]-ftswF[ksw]-ftseF[ks]+ftnwF[kw]+fbneF[kb]-fbswF[kbsw]-fbseF[kbs]+fbnwF[kbw];
      vx3=ftF[kzero]+fteF[kzero]+ftwF[kw]+ftnF[kzero]+ftsF[ks]-fbF[kb]-fbeF[kb]-fbwF[kbw]-fbnF[kb]-fbsF[kbs]+ftneF[kzero]+ftswF[ksw]+ftseF[ks]+ftnwF[kw]-fbneF[kb]-fbswF[kbsw]-fbseF[kbs]-fbnwF[kbw];

      Conc_F_NEB = c27o64 * Conc_C_NEB + c9o64 * (Conc_C_NET+Conc_C_NWB+Conc_C_SEB) + c3o64 * (Conc_C_NWT+Conc_C_SET+Conc_C_SWB) + c1o64 * Conc_C_SWT - c3o32 * (Diff_Conc_XX + Diff_Conc_YY + Diff_Conc_ZZ);

      real Diff_Conc_X_FNEB = c1o16 * (c9o1 * Conc_C_NEB - c9o1 * Conc_C_NWB + c3o1 * Conc_C_NET - c3o1 * Conc_C_NWT + c3o1 * Conc_C_SEB - c3o1 * Conc_C_SWB + c1o1 * Conc_C_SET - c1o1 * Conc_C_SWT) + c1o4 * Diff_Conc_XX;
      real Diff_Conc_Y_FNEB = c1o16 * (c9o1 * Conc_C_NEB - c9o1 * Conc_C_SEB + c3o1 * Conc_C_NET - c3o1 * Conc_C_SET + c3o1 * Conc_C_NWB - c3o1 * Conc_C_SWB + c1o1 * Conc_C_NWT - c1o1 * Conc_C_SWT) + c1o4 * Diff_Conc_YY;
      real Diff_Conc_Z_FNEB = c1o16 * (c9o1 * Conc_C_NET - c9o1 * Conc_C_NEB + c3o1 * Conc_C_NWT - c3o1 * Conc_C_NWB + c3o1 * Conc_C_SET - c3o1 * Conc_C_SEB + c1o1 * Conc_C_SWT - c1o1 * Conc_C_SWB) - c1o4 * Diff_Conc_ZZ;

      Mx = Conc_F_NEB*vx1-(c1o1+ae)/(c3o1*omegaD_F)*c1o2/*two*/*Diff_Conc_X_FNEB;
      My = Conc_F_NEB*vx2-(c1o1+ae)/(c3o1*omegaD_F)*c1o2/*two*/*Diff_Conc_Y_FNEB;
      Mz = Conc_F_NEB*vx3-(c1o1+ae)/(c3o1*omegaD_F)*c1o2/*two*/*Diff_Conc_Z_FNEB;
      Mxx= Conc_F_NEB*((c1o1+ae)/c3o1+(vx1*vx1));
      Myy= Conc_F_NEB*((c1o1+ae)/c3o1+(vx2*vx2));
      Mzz= Conc_F_NEB*((c1o1+ae)/c3o1+(vx3*vx3));
      M0 = Conc_F_NEB;

      (D7F.f[0])[kzero] = M0-Mzz-Mxx-Myy;
      (D7F.f[1])[kzero] =  c1o2*Mx +c1o2*Mxx;
      (D7F.f[2])[kw   ] =  c1o2*Mxx-c1o2*Mx;
      (D7F.f[3])[kzero] =  c1o2*My +c1o2*Myy;
      (D7F.f[4])[ks   ] =  c1o2*Myy-c1o2*My;
      (D7F.f[5])[kzero] =  c1o2*Mzz+c1o2*Mz;
      (D7F.f[6])[kb   ] = -c1o2*Mz +c1o2*Mzz;

   }
